
public class Concatation {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=20;
		int b=10;
		System.out.println(a+b+" value is "+a+b);
		System.out.println(a+b+" value is "+(a+b));
		System.out.println();
		System.out.println(a-b+" value is "+(a-b));
		System.out.println(a*b+" value is "+a*b);
		System.out.println();
		System.out.println(a%2+" value is "+a%2);
		System.out.println();
		System.out.println(a/2+" value is "+a/2);

	}

}
